function findSlope($modifiedInput, $right, $down) {
    $lineNr = 0;
    $posNr = 0;
    $treeCount = 0;
    while (true) {
        $lineNr += $down;
        $posNr += $right;
        if (!isset($modifiedInput[$lineNr][$posNr])) {
            break;
        }
        if ($modifiedInput[$lineNr][$posNr] == '#') {
            $treeCount++;
        }
    }

    return $treeCount;
}

